const Product = ({post}) => {
  return(
    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src = {post.image} alt="img" />        
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      <button>
        {
          false ? <p>Remove Items</p> : <p>Add to Cart</p>
        }
      </button>
    </div>

  );
};

export default Product;
